css:
.styleguide {
  position: relative;
  display: flex;
  flex-direction: column;
}
.styleguide a {
  text-decoration: underline;
}
.styleguide-controls {
  position: absolute;
  right: 16px;
  top: 16px;
  z-index: 100;
}
.styleguide-header {
  padding: 24px;
  border-bottom: 1px solid #2a2f38;
}
.styleguide-header h1 {
  font-size: 2em;
  margin: 0 0 .5em 0;
  color: #b7c5d3;
}
.styleguide-header p {
  font-size: 1.2em;
}
.styleguide-header p:last-of-type {
  margin-bottom: 0;
}
.styleguide-sections {
  flex: 1;
  overflow: auto;
}
.styleguide-sections > section {
  background-color: #14181d;
  padding: 0;
  border-bottom: 1px solid #2a2f38;
  border-top: none;
}
.styleguide-sections > section:last-child {
  margin-bottom: 0;
}
.styleguide-sections > section.collapsed {
  background-color: #181d23;
}
.styleguide-sections > section.collapsed > .section-heading {
  display: block;
  margin: 0;
  padding-bottom: 24px;
  color: #666d81;
}
.styleguide-sections > section.collapsed > .section-heading:hover {
  color: #b7c5d3;
  background-color: #1a2026;
}
.styleguide-sections > section.collapsed > .section-heading:active {
  background-color: #181d23;
}
.styleguide-sections > section.collapsed > * {
  display: none;
}
.styleguide-sections .section-heading.section-heading {
  padding: 24px 24px 0 24px;
  cursor: pointer;
  font-weight: normal;
  font-size: 1.8em;
  color: #b7c5d3;
}
.styleguide-sections section > h2 {
  font-size: 1.5em;
  line-height: 1.2;
  margin: 1em 24px 0 24px;
  color: #b7c5d3;
}
.styleguide-sections section > p {
  font-size: 1.1em;
  margin: 0.5em 24px 1em 24px;
}
.styleguide .example {
  display: flex;
  flex-wrap: wrap;
  border-radius: 0px;
  padding: 8px;
}
.styleguide .example .example-rendered,
.styleguide .example .example-code {
  position: relative;
  flex: 1 1 300px;
  min-width: 0;
  margin: 8px;
  border-radius: 0px;
  border: 1px solid #2a2f38;
}
.styleguide .example .example-rendered {
  padding: 16px;
  background: #181d23;
  overflow: hidden;
}
.styleguide .example .example-code {
  background-color: #fff;
}
.styleguide .example .example-code pre {
  border: none;
  background-color: inherit;
}
