#linter-tooltip {
  margin-top: 3px;
  max-width: 64em;
}
#linter-tooltip linter-message.error {
  color: rgba(255, 255, 255, 0.65);
  background-color: #193549;
  border-left: 8px solid #EF5350;
  border-right: 1px solid rgba(255, 255, 255, 0.05);
}
#linter-tooltip linter-message.error:first-child {
  border-top: 1px solid rgba(255, 255, 255, 0.05);
}
#linter-tooltip linter-message.error:last-child {
  border-bottom: 1px solid rgba(255, 255, 255, 0.05);
}
#linter-tooltip linter-message.error:last-child::before {
  border-color: transparent transparent #EF5350 #EF5350;
}
#linter-tooltip linter-message.error a {
  color: rgba(255, 255, 255, 0.65);
}
#linter-tooltip linter-message.warning {
  color: rgba(255, 255, 255, 0.65);
  background-color: #193549;
  border-left: 8px solid #FFCA28;
  border-right: 1px solid rgba(255, 255, 255, 0.05);
}
#linter-tooltip linter-message.warning:first-child {
  border-top: 1px solid rgba(255, 255, 255, 0.05);
}
#linter-tooltip linter-message.warning:last-child {
  border-bottom: 1px solid rgba(255, 255, 255, 0.05);
}
#linter-tooltip linter-message.warning:last-child::before {
  border-color: transparent transparent #FFCA28 #FFCA28;
}
#linter-tooltip linter-message.warning a {
  color: rgba(255, 255, 255, 0.65);
}
#linter-tooltip linter-message.info {
  color: rgba(255, 255, 255, 0.65);
  background-color: #193549;
  border-left: 8px solid #64B5F6;
  border-right: 1px solid rgba(255, 255, 255, 0.05);
}
#linter-tooltip linter-message.info:first-child {
  border-top: 1px solid rgba(255, 255, 255, 0.05);
}
#linter-tooltip linter-message.info:last-child {
  border-bottom: 1px solid rgba(255, 255, 255, 0.05);
}
#linter-tooltip linter-message.info:last-child::before {
  border-color: transparent transparent #64B5F6 #64B5F6;
}
#linter-tooltip linter-message.info a {
  color: rgba(255, 255, 255, 0.65);
}
#linter-tooltip linter-message.info::before {
  opacity: 0;
}
#linter-tooltip linter-message:last-child::before {
  content: "";
  position: absolute;
  top: -4px;
  left: 0;
  border: 4px solid;
}
#linter-tooltip linter-message a {
  font-weight: bold;
}
#linter-tooltip linter-message p {
  margin-bottom: 0;
}
#linter-tooltip linter-message .linter-line {
  display: block;
}
#linter-tooltip linter-message .badge {
  color: #FFFFFF;
}
linter-message {
  display: block;
  padding: 2px 5px;
}
.icon.linter-icon::before {
  font-size: 14px;
}
atom-text-editor.editor .linter-row {
  /* Take up the full allowed width */
  left: 0;
  right: 0;
  /* Align the linter dot in the middle */
  display: flex;
  align-items: center;
  justify-content: center;
}
.linter-ui-default-fix-btn {
  color: #8BC34A;
  background-color: #193549;
  border-color: transparent;
  border-radius: 0.125rem;
  margin: 0 10px;
  padding: 5px 10px;
  line-height: 100%;
  font-size: 12px;
  vertical-align: middle;
}
.linter-gutter.linter-gutter-info {
  color: #64B5F6;
  z-index: -1;
}
.linter-gutter.linter-gutter-warning {
  color: #FFCA28;
  z-index: 0;
}
.linter-gutter.linter-gutter-error {
  color: #EF5350;
  z-index: 1;
}
atom-text-editor.editor .linter-highlight.linter-info,
.linter-highlight.linter-info {
  background-image: linear-gradient(45deg, transparent 65%, #64B5F6 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #64B5F6 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #64B5F6 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #64B5F6 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
atom-text-editor.editor .linter-highlight.linter-warning,
.linter-highlight.linter-warning {
  background-image: linear-gradient(45deg, transparent 65%, #FFCA28 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #FFCA28 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #FFCA28 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #FFCA28 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
atom-text-editor.editor .linter-highlight.linter-error,
.linter-highlight.linter-error {
  background-image: linear-gradient(45deg, transparent 65%, #EF5350 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #EF5350 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #EF5350 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #EF5350 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
.tree-view span.name {
  position: relative;
}
.tree-view .linter-info {
  top: 0;
  left: 1.8rem;
  width: calc(100% - 1.8rem);
  height: 100%;
  position: absolute;
  background-image: linear-gradient(45deg, transparent 65%, #64B5F6 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #64B5F6 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #64B5F6 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #64B5F6 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
.tree-view .linter-warning {
  top: 0;
  left: 1.8rem;
  width: calc(100% - 1.8rem);
  height: 100%;
  position: absolute;
  background-image: linear-gradient(45deg, transparent 65%, #FFCA28 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #FFCA28 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #FFCA28 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #FFCA28 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
.tree-view .linter-error {
  top: 0;
  left: 1.8rem;
  width: calc(100% - 1.8rem);
  height: 100%;
  position: absolute;
  background-image: linear-gradient(45deg, transparent 65%, #EF5350 80%, transparent 90%), linear-gradient(135deg, transparent 5%, #EF5350 15%, transparent 25%), linear-gradient(135deg, transparent 45%, #EF5350 55%, transparent 65%), linear-gradient(45deg, transparent 25%, #EF5350 35%, transparent 50%);
  background-size: 8px 2px;
  background-repeat: repeat-x;
  background-position: left bottom;
}
.linter-gutter.icon::before {
  width: 100%;
  margin-right: 0px;
  font-size: 1em;
}
.linter-status-count a {
  padding: 2px;
}
.linter-status-count a::before {
  font-size: 12px;
}
.linter-status-count.hide-config,
.linter-status-count.hide-pane {
  display: none;
}
.linter-cursor-line {
  width: 100%;
  padding-left: 0;
}
linter-decoration {
  pointer-events: none;
}
